{-# LANGUAGE ScopedTypeVariables #-}
module Main where

import qualified Data.Set as Set

solveIt :: String -> Int
solveIt input = solution input [] 0

solution :: String -> String -> Int -> Int
solution [] lastFour index = -1
solution (s:ss) lastFour index | length lastFour < 4 = solution ss (lastFour ++ [s]) (index + 1)
                               | length lastFour == 4 && (areAllCharactersUnique lastFour) = index
                               | length lastFour == 4 && (not $ areAllCharactersUnique lastFour) = solution ss ((drop 1 lastFour) ++ [s]) (index + 1)


areAllCharactersUnique :: String -> Bool
areAllCharactersUnique str = length str == Set.size (Set.fromList str)

main :: IO ()
main = do
  let input = ""
  print $ solveIt input

