{-# LANGUAGE ScopedTypeVariables #-}
module Main where

import qualified Data.Set as Set

solveIt :: String -> Int
solveIt input = solution input [] 0

solution :: String -> String -> Int -> Int
solution [] last14 index = -1
solution (s:ss) last14 index | length last14 < 14 = solution ss (last14 ++ [s]) (index + 1)
                             | length last14 == 14 && (areAllCharactersUnique last14) = index
                             | length last14 == 14 && (not $ areAllCharactersUnique last14) = solution ss ((drop 1 last14) ++ [s]) (index + 1)


areAllCharactersUnique :: String -> Bool
areAllCharactersUnique str = length str == Set.size (Set.fromList str)

main :: IO ()
main = do
  let input = ""
  print $ solveIt input

